	public function __construct()
	{
		parent::__construct();
		if ($this->session->userdata("id_user")) {

        } else {
            redirect(base_url('login'));
        }
	}
	public function index($sts=0)
	{
		$data['status'] = $sts;
        $data['judul'] = 'Profile';
        $data['konten'] = $this->load->view('profile', $data, true);
		$this->load->view('template/index', $data);
	}
    
    public function edit() {
		$nama = $this->input->post('nama');
		$username = $this->input->post('username');
		$nomor_wa = $this->input->post('nomor_wa');
		$id_user = $this->session->userdata('id_user');
		$data = [];
		if($nama<>false) {
			$data = [
				'nama' => $nama
			];
		} else if($username<>false) {
			$data = [
				'username' => $username
			];
		} else if($nomor_wa<>false) {
			$data = [
				'nomor_wa' => $nomor_wa
			];
		}
		$this->db->where(['id_user' => $id_user]);
		$this->db->update('user', $data);
		redirect('profile/index/1');
	}

	public function editPassword() {
		$id_user = $this->session->userdata('id_user');
		$password = md5($this->input->post('passwordLama'));
		$query = $this->db->query("SELECT * FROM user WHERE id_user = $id_user")->row();
		if($password == $query->password) {
			$data = [
				'password' => $password
			];
			$this->db->where(['id_user' => $id_user]);
			$this->db->update('user', $data);
			$response['status'] = '200';
		} else {
			$response['status'] = '202';
			$response['message'] = 'password lama anda salah';
		}

		echo json_encode($response);
	}

	public function hapus_image() {
		$id_user = $this->session->userdata('id_user');
		// $this->delete_image($id_user);
		$data = [
			'image' => 'no-image.svg'
		];
		$this->db->where(['id_user' => $id_user]);
		$this->db->update('user', $data);
		$response['status'] = '200';

		echo json_encode($response);
	}

	public function editImage() {
		if(isset($_POST['image']))
        {
            $data = $_POST['image'];

            ////data:image/png;base64,

            $image_array_1 = explode(";", $data);

            //base64,

            $image_array_2 = explode(",", $image_array_1[1]);

            

            $data = base64_decode($image_array_2[1]);

			$image_name = 'assets/image/profile/' . time() . '.png';
			$name = time().'.png';

            file_put_contents($image_name, $data);

            // return $image_name;
        }
		$id_user = $this->session->userdata('id_user');
		// $this->delete_image($id_user);
		$data = [
			'image' => $name
		];
		$this->db->where(['id_user' => $id_user]);
		$this->db->update('user', $data);
		redirect('profile/index/1');
	}

	public function upload() {
        if(isset($_POST['image']))
        {
            $data = $_POST['image'];

            ////data:image/png;base64,

            $image_array_1 = explode(";", $data);

            //base64,

            $image_array_2 = explode(",", $image_array_1[1]);

            

            $data = base64_decode($image_array_2[1]);

            $image_name = 'assets/image/profile/' . time() . '.png';

            file_put_contents($image_name, $data);

            return $image_name;
        }
    }

	function delete_image($id) {
		$query = $this->db->query("SELECT * FROM user WHERE id_user = $id")->row();
		if(file_exists('assets/image/prodile/'.$query->image) && $query->image){
			unlink('assets/image/prodile/'.$query->image);
		}
	}
}
